commands.statement:
INSERT INTO DocumentForm ( DocumentNo , FormID , Title , CreatorName , DecisionText , NextWorkSchedule , WorkDate , Tags , CreatedAt ) VALUES ( @DocumentNo , @FormID , @Title , @CreatorName , @DecisionText , @NextWorkSchedule , @WorkDate , @Tags , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Title = @Title , CreatorName = @CreatorName , DecisionText = @DecisionText , NextWorkSchedule = @NextWorkSchedule , WorkDate = @WorkDate , Tags = @Tags , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;


DELETE FROM DocumentDetail
WHERE DocumentNo = @DocumentNo;

DELETE FROM DocumentForm
WHERE DocumentNo = @DocumentNo;
