commands.statement:
INSERT INTO DocumentForm ( DocumentNo , FormID , Customer , PublishID , PublishOwnerName , PublishName , PublishPhoneNo , PublishAddress , PublishBusiness , PublishSector , CreatorName , Tags , CreatedAt ) VALUES ( @DocumentNo , @FormID , @Customer , @PublishID , @PublishOwnerName , @PublishName , @PublishPhoneNo , @PublishAddress , @PublishBusiness , @PublishSector , @CreatorName , @Tags , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Customer = @Customer , PublishID = @PublishID , PublishOwnerName = @PublishOwnerName , PublishName = @PublishName , PublishPhoneNo = @PublishPhoneNo , PublishAddress = @PublishAddress , PublishBusiness = @PublishBusiness , PublishSector = @PublishSector , CreatorName = @CreatorName , Tags = @Tags , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;


DELETE FROM DocumentDetail
WHERE DocumentNo = @DocumentNo;

DELETE FROM DocumentForm
WHERE DocumentNo = @DocumentNo;
