commands.statement:
INSERT INTO DocumentForm ( DocumentNo , FormID , Title , Period , Purpose , PurposeRatio , Importance , GoalResult , Note , CreatorName , Tags , CreatedAt ) VALUES ( @DocumentNo , @FormID , @Title , @Period , @Purpose , @PurposeRatio , @Importance , @GoalResult , @Note , @CreatorName , @Tags , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Title = @Title , Period = @Period , Purpose = @Purpose , PurposeRatio = @PurposeRatio , Importance = @Importance , GoalResult = @GoalResult , Note = @Note , Tags = @Tags , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;


DELETE FROM DocumentDetail
WHERE DocumentNo = @DocumentNo;

DELETE FROM DocumentForm
WHERE DocumentNo = @DocumentNo;
