commands.statement:
INSERT INTO DocumentForm ( DocumentNo , FormID , Title , Period , Customer , DepartmentName , Purpose , DueDate , ReturnDate , CreatorName , Tags , CreatedAt ) VALUES ( @DocumentNo , @FormID , @Title , @Period , @Customer , @DepartmentName , @Purpose , @DueDate , @ReturnDate , @CreatorName , @Tags , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Customer = @Customer , Title = @Title , Period = @Period , Customer = @Customer , DepartmentName = @DepartmentName , Purpose = @Purpose , DueDate = @DueDate , ReturnDate = @ReturnDate , CreatorName = @CreatorName , Tags = @Tags , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;


DELETE FROM DocumentDetail
WHERE DocumentNo = @DocumentNo;

DELETE FROM DocumentForm
WHERE DocumentNo = @DocumentNo;
