SELECT DF.DocumentNo , DF.FormID , DF.Title , DF.Period , DF.GoalResult , DF.CreatorName , DF.Tags , DF.CreatedAt FROM DocumentForm DF WHERE DF.DocumentNo = @DocumentNo AND DF.FormID = @FormID;
SELECT DD.DetailNo , DD.DocumentNo , DD.ExecutionWorkName , DD.Objective , DD.KeyResult , DD.OwenerName FROM DocumentForm DF INNER JOIN DocumentDetail DD ON DF.DocumentNo = DD.DocumentNo WHERE DD.DocumentNo = @DocumentNo AND DF.FormID = @FormID AND DD.DetailCategory = 'DetailItems1';
SELECT DD.DetailNo , DD.DocumentNo , DD.WorkDate , DD.ExecutionWorkName , DD.Work1 , DD.Work2 , DD.Work3 , DD.Work4 FROM DocumentForm DF INNER JOIN DocumentDetail DD ON DF.DocumentNo = DD.DocumentNo WHERE DD.DocumentNo = @DocumentNo AND DF.FormID = @FormID AND DD.DetailCategory = 'DetailItems2';

INSERT INTO DocumentForm ( DocumentNo , FormID , Title , Period , GoalResult , CreatorName , Tags , CreatedAt ) VALUES ( @DocumentNo , @FormID , @Title , @Period , @GoalResult , @CreatorName , @Tags , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Title = @Title , Period = @Period , GoalResult = @GoalResult , CreatorName = @CreatorName , Tags = @Tags , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;


DELETE FROM DocumentDetail
WHERE DocumentNo = @DocumentNo;

DELETE FROM DocumentForm
WHERE DocumentNo = @DocumentNo;
