commands.statement:
INSERT INTO DocumentForm ( DocumentNo , FormID , Title , Period , ProgressText , DecisionText , NextWorkSchedule , Contents , CreatorName , Tags , CreatedAt ) VALUES ( @DocumentNo , @FormID , @Title , @Period , @ProgressText , @DecisionText , @NextWorkSchedule , @Contents , @CreatorName , @Tags , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Title = @Title , Period = @Period , ProgressText = @ProgressText , DecisionText = @DecisionText , NextWorkSchedule = @NextWorkSchedule , Contents = @Contents , CreatorName = @CreatorName , Tags = @Tags , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;
