commands.statement:
INSERT INTO DocumentForm ( DocumentNo , FormID , Title , CreatorName , Contents , WorkDate , CreatedAt ) VALUES (
@DocumentNo , @FormID , @Title , @CreatorName , @Contents , @WorkDate , DATETIME('NOW', 'localtime') );
UPDATE DocumentForm SET FormID = @FormID , Title = @Title , CreatorName = @CreatorName , Contents = @Contents , WorkDate = @WorkDate , ModifiedAt = DATETIME('NOW', 'localtime') WHERE DocumentNo = @DocumentNo;
SELECT @DocumentNo AS DocumentNo;
