DELETE FROM RefreshToken WHERE UserAccountID = (SELECT UserAccountID FROM UserAccount WHERE ApplicationID = @ApplicationID AND UserID = @UserID);
DELETE FROM UserAccount WHERE ApplicationID = @ApplicationID AND UserID = @UserID;
INSERT INTO UserAccount ( UserAccountID , ApplicationID , UserID , UserName , Email , Celluar , PositionName , DepartmentName , CompanyName , BirthDate , Address , Gender , IPAddress , Roles , Claims , ExtendOption , CreatedMemberNo , CreatedAt ) VALUES ( @UserAccountID , @ApplicationID , @UserID , @UserName , @Email , @Celluar , @PositionName , @DepartmentName , @CompanyName , @BirthDate , @Address , @Gender , @IPAddress , @Roles , @Claims , @ExtendOption , @CreatedMemberNo , DATETIME('now', 'localtime') );
